mapper.update:
update easybuy_news
set title = #{title,jdbcType=VARCHAR},
  content = #{content,jdbcType=VARCHAR},
  createTime = #{createtime,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, title, content, createTime
from easybuy_news
where id = #{id,jdbcType=INTEGER}
